mapper.select:
select * from housedealforaptdeal 



			where dong like CONCAT('%',#{word},'%') 


			where AptName like CONCAT('%',#{word},'%')  


			where #{key} = #{word}


	order by no asc limit #{currentPage}, #{sizePerPage}

mapper.update:
	update ssafy_member set
         userid= #{userid}, username= #{username}, userpwd= #{userpwd}, email= #{email}, address= #{address} 
         where userid = #{userid}

mapper.select:
select username, userid, email,userpwd,address 
		from ssafy_member
		where userid = #{userid} and userpwd = #{userpwd}

select userpwd 
		from ssafy_member
		where userid = #{userid} and username = #{username} and email =#{email}
